<script>
    /* Preview background */
	function readURL(input) {
		if (input.files && input.files[0]) {
			var reader = new FileReader();

			reader.onload = function (e) {
				$('.head_pro.bg_prof').css('background', 'url(' + e.target.result + ') center center');
			};
			reader.readAsDataURL(input.files[0]);
		}
	}

    /* FILE NAME BACKGROUND */
	$("input[id='file_name']").change(function (e) {
		var $this = $(this);
		$this.next().html($this.val().split('\\').pop());
	});

    /* Preview logo */
	function readURLlogo(input) {
		if (input.files && input.files[0]) {
			var reader = new FileReader();

			reader.onload = function (e) {
				$('.logo_ent').prop('src', e.target.result);
			};
			reader.readAsDataURL(input.files[0]);
		}
	}


    /* Selection bleue onglet*/
	$(document).ready(function () {
		if (document.location == '<?php echo base_url() . "entreprise/" . $BusinessEntreprise->getAlias(); ?>') {
			$(".aprop").addClass("active");
		} else if (document.location == '<?php echo base_url() . "entreprise/" . $BusinessEntreprise->getAlias() . "/offresdemploi"; ?>') {
			$(".offredemp").addClass("active");
		}
	});

</script>
<script>
	window.onscroll = function () {
		myFunction()
	};

	var navbar  = document.getElementById("navbarEnt");
	var menufix = document.getElementById("menufix");
	var logofix = document.getElementById("logofix");
	var sticky  = navbar.offsetTop;

	function myFunction() {
		if (window.pageYOffset >= sticky) {
			navbar.classList.add("sticky");
			menufix.classList.add("stick");
			logofix.classList.add("logoStick");

		} else {
			navbar.classList.remove("sticky");
			menufix.classList.remove("stick");
			logofix.classList.remove("logoStick");
		}
	}
</script>
